var cities = document.getElementById("x_partner_city");
var districts = document.getElementById("x_partner_district");
var wards = document.getElementById("x_partner_ward");
var applyButton = document.getElementById("apply-btn");
var addRelativeRow = document.getElementById("add_relative_row_btn");
var addStudyRow = document.getElementById("add_study_row_btn");
var addRow = document.getElementById("add_row_btn");
var relative = document.getElementById("x_partner_relative");
var exps = document.getElementById("x_partner_experiences");
var study = document.getElementById("x_partner_study");
let responseData;

var Parameter = {
    url: "https://raw.githubusercontent.com/kenzouno1/DiaGioiHanhChinhVN/master/data.json",
    method: "GET",
    responseType: "application/json",
};

var promise = axios(Parameter);
promise.then(function (result) {
    renderCity(result.data);
    responseData = result.data;
});
function renderCity(data) {
    for (const x of data) {
        cities.options[cities.options.length] = new Option(x.Name, x.Id);
    }
};

cities.onchange = function () {
    districts.length = 1;
    wards.length = 1;
    if(this.value != ""){
        let citiesResult = responseData.filter(n => n.Id === this.value);
        for (const k of citiesResult[0].Districts) {
            districts.options[districts.options.length] = new Option(k.Name, k.Id);
        }
    }
};
districts.onchange = function () {
    wards.length = 1;
    let dataCity = responseData.filter((n) => n.Id === cities.value);
    if (this.value != "") {
        let dataWards = dataCity[0].Districts.filter(n => n.Id === this.value)[0].Wards;
        for (const w of dataWards) {
            wards.options[wards.options.length] = new Option(w.Name, w.Id);
        }
    }
};

applyButton.onclick = function () {
    cities.options[cities.selectedIndex].value = cities.options[cities.selectedIndex].text;
    if(districts.length !== 0)
        districts.options[districts.selectedIndex].value = districts.options[districts.selectedIndex].text;
    if(wards.length !== 0)
        wards.options[wards.selectedIndex].value = wards.options[wards.selectedIndex].text;

    var name = document.getElementById("recruitment1").value;
    var dob = document.getElementById("x_partner_date_of_birth").value;
    var gender = document.querySelector('input[name="x_partner_gender"]:checked');
    var eth = document.getElementById("x_partner_ethnicity").value;
    var religion = document.getElementById("x_partner_religion").value;
    var address = document.getElementById("x_partner_address").value;
