    var iii = document.getElementById("iii");
    var v = document.getElementById("v");
    i.className = "";
    ii.className = "";
    iii.className = "";
    v.className = "";

    if(name.length==0 || dob.length==0 || gender===null || eth.length==0 || religion.length==0 ||
    districts.length==0 || wards.length==0 || address.length==0 || email.length==0 || ID.length==0 ||
    phone.length==0 || marital === null || emer_contact.length==0 || cv==0){
        i.className = "fa fa-close";
