    if(study.value.length==0){
        iii.className = "fa fa-close";
        iii.style = "color:red"
    } else {
        iii.className = "fa fa-check";
        iii.style = "color:green"
    }

    if(rfw.length==0 || tcw.length==0 || se.length==0 || cr.length==0 || strengths.length==0 ||
    weaknesses.length==0 || pff.length==0 || htn.length==0){
        v.className = "fa fa-close";
        v.style = "color:red"
    } else {
        v.className = "fa fa-check";
        v.style = "color:green"
    }

    if(name.length==0 || dob.length==0 || gender===null || eth.length==0 || religion.length==0 ||
    districts.length==0 || wards.length==0 || address.length==0 || email.length==0 || ID.length==0 ||
    phone.length==0 || marital === null || emer_contact.length==0 || cv==0 || rfw.length==0 || tcw.length==0 || se.length==0 || cr.length==0 || strengths.length==0 ||
    weaknesses.length==0 || pff.length==0 || htn.length==0)
    {
    ;
    }else{
        var oldRelativeValue = relative.value;
            relative.value = "<table class"+"="+"'table' >"+
                "<thead class"+"="+"'table-light'>"+
                    "<tr>"+
                        "<th style"+ "="+"'width:25%' > "+"Tên"+"</th>"+
                        "<th style"+ "="+"'width:15%' > " + "Quan hệ" + "</th>"+
                        "<th style"+ "="+"'width:15%' > "+ "Tuổi" + "</th>"+
