addRelativeRow.onclick = function() {
  var relative_ten = document.getElementById("relative_ten").value;
  var relative_qh = document.getElementById("relative_qh").value;
  var relative_tuoi = document.getElementById("relative_tuoi").value;
  var relative_sdt = document.getElementById("relative_sdt").value;
  var relative_nghenghiep = document.getElementById("relative_nghenghiep").value;
  if(relative_ten.length==0 || relative_qh.length==0){
      alert("Không thể thêm! Tên và Quan hệ không thể để trống");
  }else{
      var table = document.getElementById("relativeTable").getElementsByTagName('tbody')[0];
      var row = table.insertRow(-1);
      var cell1 = row.insertCell(0);
      var cell2 = row.insertCell(1);
      var cell3 = row.insertCell(2);
      var cell4 = row.insertCell(3);
      var cell5 = row.insertCell(4);
      cell1.innerHTML = relative_ten;
      cell2.innerHTML = relative_qh;
      cell3.innerHTML = relative_tuoi;
      cell4.innerHTML = relative_sdt;
      cell5.innerHTML = relative_nghenghiep;
      var oldRelative = relative.value;
      relative.value = oldRelative+"<tr>"+"<td>"+relative_ten+"</td>"+"<td>"+relative_qh+"</td>"+"<td>"+ relative_tuoi+"</td>"+"<td>" + relative_sdt+"</td>"+"<td>"+ relative_nghenghiep +"</td>"+"</tr>";
      document.getElementById("relativeModalForm").reset();
  }
}

addStudyRow.onclick = function() {
  var study_tu = document.getElementById("study_tu").value;
  var study_den = document.getElementById("study_den").value;
  var study_bac = document.getElementById("study_bac").value;
  var study_truong = document.getElementById("study_truong").value;
  var study_ten = document.getElementById("study_ten").value;
  var study_loai = document.getElementById("study_loai").value;
var study_namcap = document.getElementById("study_namcap").value;
  if(study_tu.length==0 || study_den.length==0 || study_bac.length==0 || study_truong.length ==0 || study_ten.length==0 || study_loai.length==0 || study_namcap.length==0){
      alert("Vui lòng nhập đầy đủ thông tin");
  }else{
      var table = document.getElementById("studyTable").getElementsByTagName('tbody')[0];
      var row = table.insertRow(-1);
      var cell1 = row.insertCell(0);
      var cell2 = row.insertCell(1);
      var cell3 = row.insertCell(2);
      var cell4 = row.insertCell(3);
      var cell5 = row.insertCell(4);
      var cell6 = row.insertCell(5);
        var cell7 = row.insertCell(6);
      cell1.innerHTML = study_tu;
      cell2.innerHTML = study_den;
      cell3.innerHTML = study_bac;
      cell4.innerHTML = study_truong;
      cell5.innerHTML = study_ten;
      cell6.innerHTML = study_loai;
        cell7.innerHTML = study_namcap;
      var oldStudy = study.value;
      study.value = oldStudy+"<tr>"+"<td>"+study_tu+"</td>"+"<td>"+study_den+"</td>"+"<td>"+ study_bac+"</td>"+"<td>" + study_truong+"</td>"+"<td>"+ study_ten +"</td>"+"<td>"+ study_loai +"</td>"+"<td>"+ study_namcap +"</td>"+"</tr>";
      document.getElementById("studyModalForm").reset();
  }
}

addRow.onclick = function() {
  var tu = document.getElementById("tu").value;
  var den = document.getElementById("den").value;
  var coquan = document.getElementById("coquan").value;
  var chucvu = document.getElementById("chucvu").value;
  var congviec = document.getElementById("congviec").value;
  var thanhtich = document.getElementById("thanhtich").value;
  if(tu.length==0 || den.length==0 || coquan.length==0 || chucvu.length ==0 || congviec.length==0 || thanhtich.length==0 ){
      alert("Vui lòng nhập đầy đủ thông tin");
  }else{
      var table = document.getElementById("workTable").getElementsByTagName('tbody')[0];
      var row = table.insertRow(-1);
      var cell1 = row.insertCell(0);
      var cell2 = row.insertCell(1);
      var cell3 = row.insertCell(2);
      var cell4 = row.insertCell(3);
      var cell5 = row.insertCell(4);
      var cell6 = row.insertCell(5);
      cell1.innerHTML = tu;
      cell2.innerHTML = den;
      cell3.innerHTML = coquan;
      cell4.innerHTML = chucvu;
      cell5.innerHTML = congviec;
      cell6.innerHTML = thanhtich;
      var old = exps.value;
      exps.value = old+"<tr>"+"<td>"+tu+"</td>"+"<td>"+den+"</td>"+"<td>"+ coquan+"</td>"+"<td>" + chucvu+"</td>"+"<td>"+ congviec +"</td>"+"<td>"+ thanhtich +"</td>"+"</tr>";
      document.getElementById("workModalForm").reset();
  }
}
